mapper.select:

select

from sys_resource r
where r.id = #{id,jdbcType=BIGINT}

mapper.update:
UPDATE sys_resource SET
lft = lft + #{step,jdbcType=INTEGER}
WHERE 1 = 1

  AND lft >= #{from,jdbcType=BIGINT}


  AND lft <= #{to,jdbcType=BIGINT}

UPDATE sys_resource SET
rt = rt + #{step,jdbcType=INTEGER}
WHERE 1 = 1

  AND rt >= #{from,jdbcType=BIGINT}


  AND rt <= #{to,jdbcType=BIGINT}

UPDATE sys_resource SET
level = level + #{levelStep,jdbcType=INTEGER}
WHERE 1 = 1

  AND lft >= #{from,jdbcType=BIGINT}


  AND rt <= #{to,jdbcType=BIGINT}
